INSERT INTO `config_info`(`id`, `data_id`, `group_id`, `content`, `md5`, `gmt_create`, `gmt_modified`, `src_user`, `src_ip`, `app_name`, `tenant_id`, `c_desc`, `c_use`, `effect`, `type`, `c_schema`) VALUES (16, 'application-dev.yml', 'DEFAULT_GROUP', 'behappy:\n  aliyun:\n    access-key: \n    access-secret: \n  oss:\n    bucket: \n    endpoint: \n  sms:\n    region-id: \n    sign-name: \n    template-code: \n  wb:\n    client-id: \n    client-secret: \n  wx: \n    notify-url: https://xiaowu-gulimall.loca.lt/pay/notify\n  alipay:\n    app-id: \n    merchant-private-key: \n    alipay-public-key: \n    notify-url: https://xiaowu-gulimall.loca.lt/payed/notify\n  rabbitmq:\n    host: 192.168.56.100\n    port: 5672\n    virtual-host: /\n    username: root\n    password: root\n  mysql:\n    password: root\n    username: root\n    host: 192.168.56.100:3306\n    db:\n      oms: gulimall_oms\n      pms: gulimall_pms\n      sms: gulimall_sms\n      ums: gulimall_ums\n      wms: gulimall_wms\n      seata: seata\nmanagement:\n  server:\n    # 启用独立运维端口\n    port: 50000\n  metrics:\n    tags:\n      application: ${spring.application.name}\n  endpoints:\n    web:\n      exposure:\n        include: \"*\"\n  endpoint:\n    health:\n      show-details: ALWAYS\n      probes:\n        enabled: true\nspring:\n  # 开发环境，方便调试\n  thymeleaf:\n    cache: false\n  application:\n    # 此处填写网关地址，会在spring doc中使用\n    gateway-host: http://localhost:88\n  lifecycle:\n    # 停机过程超时时长设置30s，超过30s，直接停机\n    timeout-per-shutdown-phase: 30s\nserver:\n  # 默认为IMMEDIATE，表示立即关机；GRACEFUL表示优雅关机\n  shutdown: graceful', '1e9e5a1ed164aeaaf269f01d47b12afa', '2023-03-06 20:28:44', '2023-08-01 05:09:40', 'nacos', '192.168.56.1', 'ALL', '1c3bfebf-a571-44d6-9b75-70fd124cbcf7', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (17, 'redis-dev.yml', 'DEFAULT_GROUP', 'spring:\n  redis:\n    redisson:\n      config: |-\n        # 例: 单节点设置\n        singleServerConfig:\n          # （节点地址）redis://-非ssl,rediss://-ssl\n          address: redis://192.168.56.100:6379\n          database: 0\n          password: null\n          # 如果当前连接池里的连接数量超过了`最小空闲连接数`，而同时有连接空闲时间超过了该数值，那么这些连接将会自动被关闭，并从连接池里去掉。时间单位是毫秒。\n          idleConnectionTimeout: 10000\n          # 同节点建立连接时的等待超时。时间单位是毫秒。\n          connectTimeout: 10000\n          # 等待节点回复命令的时间。该时间从命令发送成功时开始计时。\n          timeout: 3000\n          # 如果尝试达到 retryAttempts（命令失败重试次数） 仍然不能将命令发送至某个指定的节点时，将抛出错误。如果尝试在此限制之内发送成功，则开始启用 timeout（命令等待超时） 计时。\n          retryAttempts: 3\n          # 在某个节点执行相同或不同命令时，连续 失败 failedAttempts（执行失败最大次数） 时，该节点将被从可用节点列表里清除，直到 reconnectionTimeout（重新连接时间间隔） 超时以后再次尝试。\n          retryInterval: 1500\n          # 在Redis节点里显示的客户端名称。\n          clientName: null\n          # 发布和订阅连接的最小空闲连接数 默认1\n          subscriptionConnectionMinimumIdleSize: 1\n          # 发布和订阅连接池大小 默认50\n          subscriptionConnectionPoolSize: 50\n          # 单个连接最大订阅数量 默认5\n          subscriptionsPerConnection: 5\n          # 最小空闲连接数 默认32，现在暂时不需要那么多的线程\n          connectionMinimumIdleSize: 4\n          # 连接池大小,在启用该功能以后，Redisson将会监测DNS的变化情况。\n          connectionPoolSize: 64\n          # 监测DNS的变化情况的时间间隔。\n          dnsMonitoringInterval: 5000\n        # 这个线程池数量被所有RTopic对象监听器，RRemoteService调用者和RExecutorService任务共同共享。\n        threads: 0\n        # 这个线程池数量是在一个Redisson实例内，被其创建的所有分布式数据类型和服务，以及底层客户端所一同共享的线程池里保存的线程数量。\n        nettyThreads: 0\n        # (如果不配置则默认为Kryo5Codec方式)，当前支持JsonJacksonCodec和Kryo5Codec序列化方式\n        codec: !<org.redisson.codec.JsonJacksonCodec> {}\n        transportMode: NIO', 'c292a24ca2a04d189dfcf3bb2281fb64', '2023-03-06 20:28:44', '2023-03-08 01:39:33', 'nacos', '192.168.56.1', 'Redis', '1c3bfebf-a571-44d6-9b75-70fd124cbcf7', 'Redis共享配置', '', '', 'yaml', '');
