     rule-type: system\n            data-type: json\n            data-id: ${spring.application.name}-system-rules\n            namespace: 4e8525e0-335b-46c1-b61e-1a46c5f0a147\n    gateway:\n      discovery:\n        locator:\n          enabled: true\n          lower-case-service-id: true\n      routes:\n        - id: behappy-product\n          uri: lb://behappy-product\n          predicates:\n            - Path=/api/product/**\n          filters:\n            - RewritePath=/api/(?<segment>/?.*),/$\\{segment}\n        - id: behappy-coupon\n          uri: lb://behappy-coupon\n          predicates:\n            - Path=/api/coupon/**\n          filters:\n            - RewritePath=/api/(?<segment>/?.*),/$\\{segment}\n        - id: behappy-third-party\n          uri: lb://behappy-third-party\n          predicates:\n            - Path=/api/thirdparty/**\n          filters:\n            - RewritePath=/api/thirdparty/(?<segment>/?.*),/$\\{segment}\n        - id: behappy-member\n          uri: lb://behappy-member\n          predicates:\n            - Path=/api/member/**\n          filters:\n            - RewritePath=/api/(?<segment>/?.*),/$\\{segment}\n        - id: behappy-ware\n          uri: lb://behappy-ware\n          predicates:\n            - Path=/api/ware/**\n          filters:\n            - RewritePath=/api/(?<segment>/?.*),/$\\{segment}\n        - id: behappy-order\n          uri: lb://behappy-order\n          predicates:\n            - Path=/api/order/**\n          filters:\n            - RewritePath=/api/(?<segment>/?.*),/$\\{segment}\n        # 固定路由转发配置\n        - id: openapi\n          uri: lb://behappy-gateway\n          predicates:\n            - Path=/v3/api-docs/**\n          filters:\n            - RewritePath=/v3/api-docs/(?<path>.*), /$\\{path}/$\\{path}/v3/api-docs\n        - id: gulimall-auth-host\n          uri: lb://behappy-auth-server\n          predicates:\n            - Host=auth.gulimall.com\n        - id: gulimall-fast\n          uri: http://192.168.3.153:8080\n          predicates:\n            - Path=/api/**\n          filters:\n            - RewritePath=/api/(?<segment>/?.*), /renren-fast/$\\{segment}\n        - id: gulimall-search-host\n          uri: lb://behappy-search\n          predicates:\n            - Host=search.gulimall.com\n        - id: gulimall-cart-host\n          uri: lb://behappy-cart\n          predicates:\n            - Host=cart.gulimall.com\n        - id: gulimall-order-host\n          uri: lb://behappy-order\n          predicates:\n            - Host=order.gulimall.com\n        - id: gulimall-member-host\n          uri: lb://behappy-member\n          predicates:\n            - Host=member.gulimall.com\n        - id: gulimall-seckill-host\n          uri: lb://behappy-seckill\n          predicates:\n            - Host=seckill.gulimall.com\n        - id: gulimall-host\n          uri: lb://behappy-product\n          predicates:\n            - Host=gulimall.com,item.gulimall.com\n', '53bb2e30b25c291bbc56358b2e3a7665', '2023-03-06 20:28:44', '2023-07-17 04:39:51', 'nacos', '192.168.56.1', 'behappy-gateway', '1c3bfebf-a571-44d6-9b75-70fd124cbcf7', '', '', '', 'yaml', '');
