INSERT INTO `config_info` VALUES (28, 'sentinel-dev.yml', 'DEFAULT_GROUP', 'feign:\n  sentinel:\n    enabled: true\nspring: \n  cloud:\n    openfeign:\n      okhttp:\n        enabled: false\n      httpclient:\n        enabled: true\n        max-connections: 200 # 最大的连接数\n        max-connections-per-route: 50 # 每个路径的最大连接数\n      client:\n        config:\n          default: # default全局的配置\n            loggerLevel: basic # 日志级别，BASIC就是基本的请求和响应信息\n            connectTimeout: 10000\n            readTimeout: 10000\n      compression:\n        request:\n          enabled: true\n          # 期间遇到问题：Illegal character ((CTRL-CHAR, code 31))，原因是feign调用时参数过大，经过网络传输到达后端服务时数据丢失导致解析失败\n          min-request-size: 20480\n        response:\n          enabled: true\n      # https://github.com/alibaba/spring-cloud-alibaba/issues/3024\n      lazy-attributes-resolution: true\n    sentinel:\n      # 取消Sentinel控制台懒加载\n      eager: true\n      transport:\n        dashboard: 192.168.56.100:8858\n      datasource:\n        flow-ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr:192.168.56.100:8848}\n            username: ${spring.cloud.nacos.username:nacos}\n            password: ${spring.cloud.nacos.password:nacos}\n            rule-type: flow\n            data-type: json\n            data-id: ${spring.application.name}-flow-rules\n            namespace: 4e8525e0-335b-46c1-b61e-1a46c5f0a147\n        degrade-ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr:192.168.56.100:8848}\n            username: ${spring.cloud.nacos.username:nacos}\n            password: ${spring.cloud.nacos.password:nacos}\n            rule-type: degrade\n            data-type: json\n            data-id: ${spring.application.name}-degrade-rules\n            namespace: 4e8525e0-335b-46c1-b61e-1a46c5f0a147\n        system-ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr:192.168.56.100:8848}\n            username: ${spring.cloud.nacos.username:nacos}\n            password: ${spring.cloud.nacos.password:nacos}\n            rule-type: system\n            data-type: json\n            data-id: ${spring.application.name}-system-rules\n            namespace: 4e8525e0-335b-46c1-b61e-1a46c5f0a147\n        param-flow-ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr:192.168.56.100:8848}\n            username: ${spring.cloud.nacos.username:nacos}\n            password: ${spring.cloud.nacos.password:nacos}\n            rule-type: param_flow\n            data-type: json\n            data-id: ${spring.application.name}-param-flow-rules\n            namespace: 4e8525e0-335b-46c1-b61e-1a46c5f0a147\n        authority-ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr:192.168.56.100:8848}\n            username: ${spring.cloud.nacos.username:nacos}\n            password: ${spring.cloud.nacos.password:nacos}\n            rule-type: authority\n            data-type: json\n            data-id: ${spring.application.name}-authority-rules\n            namespace: 4e8525e0-335b-46c1-b61e-1a46c5f0a147', '9fdb7ad1164833b04b6aef2b8da6424f', '2023-03-06 20:28:44', '2023-07-18 00:43:43', 'nacos', '192.168.56.1', 'SENTINEL', '1c3bfebf-a571-44d6-9b75-70fd124cbcf7', 'sentinel共享配置', '', '', 'yaml', '');
